import axios from 'axios';
import React from 'react';
import logo from './logo.svg';
import bulbOn from './bulb-on.png';
import bulbOff from './bulb-off.png';

import './App.css';

class App extends React.Component {
  state = {
    word: "-",
    isOn: true
  }
  async componentDidMount() {
    const config = {
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
      }
    };
    const { data } = await axios.get('http://4d776caa.ngrok.io/');
    const isOn = data.result.includes('1');
    // const productList = productword.data
    this.setState({ word: data.word, isOn })
  }

  render() {
    return (
      <div className="App" >
        <header className="App-header">
          <p>
            {this.state.word}
          </p>
          {this.state.isOn && <img src={bulbOn} className="App-logo" alt="logo" />}
          {!this.state.isOn && <img src={bulbOff} className="App-logo" alt="logo" />}
        </header>
      </div>
    );
  }
}


export default App;
