import axios from 'axios';
import React from 'react';
import logo from './logo.svg';
import bulbOn from './bulb-on.png';
import bulbOff from './bulb-off.png';

import './App.css';

class App extends React.Component {
  state = {
    word: "-",
    isOn: true,
    counting: 0,
    intervalId: 0
  }
  async componentDidMount() {
    const intervalId = setInterval(this.fetching, 5000);
    this.setState({ intervalId: intervalId });
    this.fetching()
  }
  componentWillUnmount() {
    // use intervalId from the state to clear the interval
    clearInterval(this.state.intervalId);
  }
  fetching = async () => {
    const { data } = await axios.get('http://4d776caa.ngrok.io/');
    console.log(data)
    const isOn = data.result.includes('1');
    // const productList = productword.data
    this.setState({ word: data.word, isOn, counting: this.state.counting + 1 })
  }

  // useEffect = () => {
  //   const interval = setInterval(
  //     this.fetching
  //     , 1000);
  //   return () => clearInterval(interval);
  // }


  render() {
    return (
      <div className="App" >
        <header className="App-header">
          <p>
            {this.state.word}
            {this.state.counting}
          </p>
          {this.state.isOn && <img src={bulbOn} className="App-logo" alt="logo" />}
          {!this.state.isOn && <img src={bulbOff} className="App-logo" alt="logo" />}
        </header>
      </div>
    );
  }
}


export default App;
